import java.util.Scanner;public class MultipleCatchExample {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        try {            System.out.print("Enter a positive number: ");            int num = sc.nextInt();            if (num < 0) throw new Exception("Negative number not allowed!");            if (num == 0) throw new ArithmeticException("Number cannot be zero!");            System.out.println("You entered: " + num);        }        catch (ArithmeticException e) {            System.out.println("Arithmetic Exception: " + e.getMessage());        }        catch (Exception e) {            System.out.println("General Exception: " + e.getMessage());        }        System.out.println("End of program.");        sc.close();    }}